import React, { Component } from 'react';
import { Navbar, Nav, NavItem, Button, ResponsiveEmbed, ButtonToolbar, Form, Grid, Row, FormGroup, Tab, Radio, Tabs, Col, Table, Popover, ControlLabel, MenuItem, DropdownButton, FormControl, Checkbox } from 'react-bootstrap';

import firebase from 'firebase';

import { PDFExport } from '@progress/kendo-react-pdf';
import { fire } from '../fire';

import {BootstrapTable, BootstrapButton, TableHeaderColumn, ExportCSVButton} from 'react-bootstrap-table';
import { TiArrowSortedDown, TiArrowSortedUp, TiPencil, TiTrash } from "react-icons/ti";

import domtoimage from 'dom-to-image';
import fileDownload from "js-file-download";

import SignInScreen from './SignInScreen';

import { Link } from 'react-router-dom';
import { BrowserRouter, Route } from 'react-router-dom';
import { LineChart, ReferenceArea, AreaChart, Brush, Area, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, Label} from 'recharts';













const styles = {
  topPad: {
    paddingTop: "20px"
  },
};





export default class home extends Component {


    constructor(props) {
        super(props);
        this.state = {


          //Contact Form Inputs
          chemicalName: '',
          applicationArea: '',
          applicationDescription: '',
          applicationDate: '',
          applicationAmount: '',
          applicationNotes: '',

          //Random things for changing tabs
          id: '',
          key: 1,
          idKey: '',
          page: '',
          area: '',


          //put data into tables with these
          samples: [],
          orders: [],
          orders2: [],
          dataList: [],
          filter: "",
          blobUrl: null,




        }
        //these are triggered events.  handleChange is for text box changes
        //handlesubmit is for the form being submitted.
        //every event trigger needs to be bound like is below with .bind
        //we might now have to do this anymore with the newest version of react, but i have it to be safe.
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSelect = this.handleSelect.bind(this);
        this.writeData = this.writeData.bind(this);





      }




      render() {





        return (
          <div style={styles.background} >

            <Row style={styles.background}>

              <Col xs={5} sm={5} md={5}>
                <StickyContainer style={{position: 'fixed'}}>

                <Panel style={styles.sidenav}>
                  <Panel.Body>


      <ListGroup style={styles.panelWidth}>
      <ListGroupItem >
        <NavItem componentClass={Link} href="/dashboard" to="/dashboard">
          Dashboard
        </NavItem>


      </ListGroupItem>



      <OverlayTrigger rootClose="true" trigger="click" placement="right" overlay={popoverRightSampling}>
     <ListGroupItem>Sampling</ListGroupItem>
     </OverlayTrigger>

        <OverlayTrigger rootClose="true" trigger="click" placement="right" overlay={popoverRightAsset}>
        <ListGroupItem>Asset Manager</ListGroupItem>
        </OverlayTrigger>

        <OverlayTrigger rootClose="true" trigger="click" placement="right" overlay={popoverRightDocuments}>
       <ListGroupItem>Documents</ListGroupItem>
       </OverlayTrigger>

       <OverlayTrigger rootClose="true" trigger="click" placement="right" overlay={popoverRightProfile}>
      <ListGroupItem>Profile</ListGroupItem>
      </OverlayTrigger>




    </ListGroup>
    <Button bsStyle="info" onClick={() => firebase.auth().signOut()}>Sign Out

    </Button>



                  </Panel.Body>
    </Panel>
    </StickyContainer>
    </Col>
        <Col style={styles.background} mdOffset={2} smOffset={2} xsOffset={2} xs={8} sm={8} md={8}>

          <Route exact path="/" component={home} />
          <Route path="/SignInScreen" component={SignInScreen} />



          </Col>
        </Row>








      </div>




        )
            }
          }
