import * as React from 'react'
import Box from './Box'
const update = require('immutability-helper')

const style = {
	width: 400,
}

export interface ContainerState {
	boxes: Array<{
		id: number,
		text: string
	}>
}

export default class Container extends React.Component<{}, ContainerState> {
	state = {
		boxes: [
			{
				id: 1,
				text: 'Write a cool JS library',
			},
			{
				id: 2,
				text: 'Make it generic enough',
			},
			{
				id: 3,
				text: 'Write README',
			},
			{
				id: 4,
				text: 'Create some examples',
			},
			{
				id: 5,
				text:
					'Spam in Twitter and IRC to promote it (note that this element is taller than the others)',
			},
			{
				id: 6,
				text: '???',
			},
			{
				id: 7,
				text: 'PROFIT',
			},
		],
	}

	render() {
		const { boxes } = this.state

		return (
			<div style={style}>
				{boxes.map((box, i) => (
					<Box
						key={box.id}
						index={i}
						id={box.id}
						text={box.text}
						moveBox={this.moveBox}
					/>
				))}
			</div>
		)
	}

	moveBox = (dragIndex: number, hoverIndex: number) => {
		const { voxes } = this.state
		const dragBox = boxes[dragIndex]

		this.setState(
			update(this.state, {
				boxes: {
					$splice: [[dragIndex, 1], [hoverIndex, 0, dragBox]],
				},
			}),
		)
	}
}
