import React, { Component } from 'react'
import { Navbar, Nav, Grid, Jumbotron, Row, Col, NavItem, Button, Modal, Panel } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import ReactDOM from 'react-dom';
import FileSaver from 'file-saver';
import * as jsPDF from 'jspdf';
import domtoimage from 'dom-to-image';
import fileDownload from "js-file-download";
import Request from 'superagent';

import firebase from 'firebase';
import { fire } from '../fire';


import {BootstrapTable, BootstrapButton, TableHeaderColumn} from 'react-bootstrap-table';
import { TiArrowSortedDown, TiArrowSortedUp, TiPencil, TiTrash } from "react-icons/ti";

import { LineChart, BarChart, Bar, Surface, ReferenceLine, ReferenceArea, AreaChart, Brush, Area, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend} from 'recharts';



export default class Dashboard extends Component {

  constructor(props) {
      super(props);
      this.state = {

        //The data that goes into the table showing weather data
        weatherData: [],
        iconData: [],

        id: '',
        key: 1,

        currentCity: '',
        currentTemp: '',
        currentIcon: '',
        currentDescription: '',
        currentHumidity: '',




      }

    }






    //async allows await to be used for API calls.  Without ASYNC an error will be called saying that await is a reserved word.

    //ComponentDidMount runs over and over again

  async componentDidMount() {
    let weatherData = [];
    let city = '';
    let temp = '';
    let icon = '';
    let description = '';
    let humidity = '';

    let api_callWeather =  await fetch(`http://api.openweathermap.org/data/2.5/weather?lat=${'37.997'}&lon=${'-121.71'}&units=${'imperial'}&appid=${'30573b68170d7f4400c7ac9c1c671ccf'}`);


    let weatherResponse = await api_callWeather.json();

    let symbol = weatherResponse.weather[0].icon;


    console.log(weatherResponse)

    this.setState({
      currentCity: weatherResponse.name,
      currentTemp: weatherResponse.main.temp,
      currentIcon: 'http://openweathermap.org/img/w/' + weatherResponse.weather[0].icon + '.png',

      currentDescription: weatherResponse.weather[0].main,
      currentHumidity: weatherResponse.main.humidity,

    })

console.log(this.state.currentIcon)








    this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{

      const samplesRef = fire.database().ref(`dailySamples/${user.uid}`);
      samplesRef.on('value', (snapshot) => {


    });
  });
  }



   imageFormatter = (cell, row) => {
      return (
<span><img src={cell} /></span>
)
    }



  render() {



    return (
      <Grid>
        <Row>
          <Col xs={11} sm={11} md={11} >
        <Jumbotron>
          <h1>Summer Lake</h1>
          <p>
            Homeowners Association
          </p>
          <Button onClick={this.getWeather}>Get Weather</Button>



        </Jumbotron>
        <hr></hr>

          <Row>
            <Col xs={4} sm={4} md={4} >

              <div>
      <Panel>
        <Panel.Heading>
          <Panel.Title componentClass="h3">Weather</Panel.Title>
        </Panel.Heading>
        <Panel.Body style={{textAlign: 'center'}}>
          <h3>{this.state.currentCity}</h3>
          <img src={this.state.currentIcon} />
          <p>{this.state.currentDescription}</p>
          <p>Temperature: {this.state.currentTemp}</p>
          <p>Humidity: {this.state.currentHumidity}</p>

        </Panel.Body>
      </Panel>
    </div>
    </Col>
    <Col xs={4} sm={4} md={4} >

      <div>
<Panel>
<Panel.Heading>
  <Panel.Title componentClass="h3">Panel heading with a title</Panel.Title>
</Panel.Heading>
<Panel.Body>Panel content</Panel.Body>
</Panel>
</div>
</Col>
<Col xs={4} sm={4} md={4} >

  <div>
<Panel>
<Panel.Heading>
<Panel.Title componentClass="h3">Panel heading with a title</Panel.Title>
</Panel.Heading>
<Panel.Body>Panel content</Panel.Body>
</Panel>
</div>
</Col>



  </Row>







<Row style={{paddingTop: "50px"}}>
  <Col xs={8} sm={8} md={8} >

    <BootstrapTable
    data={ this.state.weatherData }
    containerStyle={{width: '800px', overflowX: 'scroll'}}
    pagination
    >

    <TableHeaderColumn dataField='date' isKey> Date</TableHeaderColumn>
    <TableHeaderColumn dataField='temp' >Temp</TableHeaderColumn>
    <TableHeaderColumn dataField='description' >Description</TableHeaderColumn>
    <TableHeaderColumn dataField="image" dataFormat={this.imageFormatter}>Product Image</TableHeaderColumn>




    </BootstrapTable>




</Col>

</Row>







        </Col>
        </Row>
      </Grid>

    )
  }
}
