                })
                .catch(error => {
                  console.log(error);
                });


        });

          const parameter2ist1Ref = fire.database().ref(`maintenanceReport/${user.uid}`);
          parameter2ist1Ref.on('value', (snapshot) => {
            let parameterList2 = snapshot.val();
            console.log(parameterList2);

          })








    })
  }



    fillStates(itemId) {

