import React, { Component } from 'react';

import { Link } from 'react-router-dom';
import firebase from 'firebase';
import { Page, Text, View, Document, StyleSheet, Image,  PDFDownloadLink, Font,  } from '@react-pdf/renderer';
import styled from '@react-pdf/styled-components';

import './maintenanceReport.css';


import { fire } from '../../fire';


import domtoimage from 'dom-to-image';
import { SketchPicker } from 'react-color';
import fileDownload from "js-file-download";


import { ComposedChart, LineChart, LabelList, ResponsiveContainer, ReferenceArea, AreaChart, Brush, Area, Line, XAxis, YAxis, BarChart, Bar, CartesianGrid, Tooltip, Legend, Label} from 'recharts';

import { Row, Col, Tabs, Table, Divider, Tag, message, Card, Drawer, Menu, Dropdown, Button, Layout, Carousel, Form, Input, Icon, Cascader, Select, Checkbox, AutoComplete, } from 'antd';
import Highlighter from 'react-highlight-words';
import { CSVLink, CSVDownload } from "react-csv";

const TabPane = Tabs.TabPane;

const { Option } = Select;
const AutoCompleteOption = AutoComplete.Option;

const styles = StyleSheet.create({
  page: {

    backgroundColor: '#E4E4E4'
  },
  section: {
    position: 'absolute',
    left: 20,
    top: 20,
    fontSize: 35,
    fontFamily: 'Muli',

  },
  section1: {
    position: 'absolute',
    top: 20,
    left: 300,

    fontSize: 35,
    fontFamily: 'Muli',

  }
  ,
  text: {
    fontFamily: 'Roboto',
  }
});

const Heading = styled.Text`
  margin: 10px;
  font-size: 22px;
  font-family: 'Helvetica';
`;


const residences = [{
  value: 'zhejiang',
  label: 'Zhejiang',
  children: [{
    value: 'hangzhou',
    label: 'Hangzhou',
    children: [{
      value: 'xihu',
      label: 'West Lake',
    }],
  }],
}, {
  value: 'jiangsu',
  label: 'Jiangsu',
  children: [{
    value: 'nanjing',
    label: 'Nanjing',
    children: [{
      value: 'zhonghuamen',
      label: 'Zhong Hua Men',
    }],
  }],
}];



export default class maintenanceReports extends Component {


    constructor(props) {
        super(props);
        this.state = {
          userID: '',
          key: "1",
          snapArray: [],
          snapArray1: [],

          Maintenance_Item: '',
          maintenanceDate: '',
          confirmDirty: false,
          autoCompleteResult: [],


          tableKeys: [],
          searchText: '',
          selectedRowKeys: [], // Check here to configure the default column
          loading: false,

          //data results
          sampleDate: '',

          url: null,
          blob: null,
          file:null,
          blobUrl: null,


          //for drawers
          visible: false,
          visible1: false,
          visible2: false,

          //Inputs for Profile Page
          lakeName: '',
          locationCity: '',
          locationState: '',
          managementContact: '',
          hoaContact: '',
          managementContactNumber: '',
          hoaContactNumber: '',







        }

        this.handleChange = this.handleChange.bind(this);



      }

      //event triggered when text boxes of forms, values are changed
      handleChange(e) {
        const name = e.target.name;
    const value = e.target.value;
    this.setState({ [name]: value });
        this.setState({
          [e.target.name]: e.target.value
        });



      }
      //event triggered when form is submitted


     snapshotToArray(snapshot) {
        var returnArr = [];

        snapshot.forEach(function(childSnapshot) {
            var item = childSnapshot.val();
            item.key = childSnapshot.key;

            returnArr.push(item);
        });

        return returnArr;
    };





      componentDidMount(itemId, source) {

        this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{

          this.setState({
            userID: user.uid,
          })

          const parameterList1Ref = fire.database().ref(`maintenanceReport/${user.uid}`);
          parameterList1Ref.on('value', (snapshot) => {
            let snapArray = this.snapshotToArray(snapshot);
            let data = snapArray;

            let tableData1 = [];
            for (let i=0; i < snapArray.length; i++) {
            //push send this data to the back of the chartData variable above.
            tableData1.push(Object.keys(snapArray[i]));

            }

            let tableData2 = tableData1.map(function(a){return a.length;});
            tableData2.indexOf(Math.max.apply(Math, tableData2));

            let indexOfMaxValue = tableData2.reduce((iMax, x, i, arr) => x > arr[iMax] ? i : iMax, 0);

            console.log(indexOfMaxValue);

            let graphKeys = Object.keys(snapArray[indexOfMaxValue]);
             graphKeys = graphKeys.filter(e => e !== 'date');
             graphKeys = graphKeys.filter(e => e !== 'id');
             graphKeys = graphKeys.filter(e => e !== 'key');

            let tableKeys = graphKeys.map((key) =>

              {
                  return ({
                  title: key,
                  dataIndex: key,
                  key: key,
                  ...this.getColumnSearchProps(key),
                  sorter: (a, b) => a.key - b.key,
                  sortDirections: ['descend', 'ascend'],
                }
                )
            });

            tableKeys.unshift({

            title: 'Date',
            dataIndex: 'date',
            key: 'date',
            ...this.getColumnSearchProps('date'),

            sorter: (a, b) => { return a.date.localeCompare(b.date)},
            sortDirections: ['descend', 'ascend'],

            })
            tableKeys.push({

              title: 'Action',
              dataIndex: '',
              key: 'x',
              render: this.editRow.bind(this),

            })

            tableKeys.push({

              title: 'Action',
              dataIndex: '',
              key: 'y',
              render: this.deleteRow.bind(this),

            })

            console.log(tableKeys)



            this.setState({
              snapArray: data,
              tableKeys: tableKeys,
            })



             })

             const parameterList2Ref = fire.database().ref(`maintenanceList/${user.uid}`);
             parameterList2Ref.on('value', (snapshot) => {
               let maintenanceArray = this.snapshotToArray(snapshot);

               this.setState({
                 snapArray1: maintenanceArray,

               })
             })







          const profileRef = fire.database().ref(`profileInformation/${user.uid}`);
          profileRef.on('value', (snapshot) => {
            var that = this;


          this.setState({
            lakeName: snapshot.child('lakeName').val(),
            locationCity: snapshot.child('locationCity').val(),
            locationState: snapshot.child('locationState').val(),
            managementContact: snapshot.child('managementContact').val(),
            hoaContact: snapshot.child('hoaContact').val(),
            managementContactNumber: snapshot.child('managementContactNumber').val(),
            hoaContactNumber: snapshot.child('hoaContactNumber').val(),
            latitude: snapshot.child('latitude').val(),
            longitude: snapshot.child('longitude').val(),
            center: {
              lat: snapshot.child('latitude').val(),
              lng: snapshot.child('longitude').val()
            },

          });


        });



    })
  }




    handleSelect = (key) => {
  this.setState({key});
}



showDrawer = () => {
  this.setState({
    visible: true,
  });
};
onClose = () => {
  this.setState({
    visible: false,
    visible1: false,
    visible2: false,
  });
};






filter = (url) => {

  domtoimage.toBlob(document.getElementById('my-node'))
      .then((blob) => {

          const blobUrl = URL.createObjectURL(blob);
          this.setState({
            blobUrl: blobUrl,
          })
      });
}


start = () => {
  this.setState({ loading: true });
  // ajax request after empty completing
  setTimeout(() => {
    this.setState({
      selectedRowKeys: [],
      loading: false,
    });
  }, 1000);
}

onSelectChange = (selectedRowKeys) => {
  console.log('selectedRowKeys changed: ', selectedRowKeys);
  this.setState({ selectedRowKeys });
}

getColumnSearchProps = (dataIndex) => ({
    filterDropdown: ({
      setSelectedKeys, selectedKeys, confirm, clearFilters,
    }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={node => { this.searchInput = node; }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
          style={{ width: 188, marginBottom: 8, display: 'block' }}
        />
        <Button
          type="primary"
          onClick={() => this.handleSearch(selectedKeys, confirm)}
          icon="search"
          size="small"
          style={{ width: 90, marginRight: 8 }}
        >
          Search
        </Button>
        <Button
          onClick={() => this.handleReset(clearFilters)}
          size="small"
          style={{ width: 90 }}
        >
          Reset
        </Button>
      </div>
    ),
    filterIcon: filtered => <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />,
    onFilter: (value, record) => record[dataIndex].toString().toLowerCase().includes(value.toLowerCase()),
    onFilterDropdownVisibleChange: (visible) => {
      if (visible) {
        setTimeout(() => this.searchInput.select());
      }
    },

  })

  handleSearch = (selectedKeys, confirm) => {
    confirm();
    this.setState({ searchText: selectedKeys[0] });
    console.log(selectedKeys[0]);
    console.log(this.state.searchText);
  }

  handleReset = (clearFilters) => {
    clearFilters();
    this.setState({ searchText: '' });
  }

  deleteRow = (row, isSelected, e, id, key) =>
  {
    return (
      <div style={{textAlign: 'center'}}>
      <Icon type="delete" style={{fontSize: '24px'}}
      onClick={() => this.removesample(isSelected.key)}>
        Click me
      </Icon>
      </div>
    )
  }
  removesample(itemId) {
    console.log(itemId)
   const sampleRef = fire.database().ref(`/maintenanceReport/${this.state.userID}/${itemId}`);
 }


 deleteRow1 = (row, isSelected, e, id, key) =>
 {
   return (
     <div style={{textAlign: 'center'}}>
     <Icon type="delete" style={{fontSize: '24px'}}
     onClick={() => this.removesample1(isSelected.key)}>
       Click me
     </Icon>
     </div>
   )
 }
 removesample1(itemId) {

  const sampleRef = fire.database().ref(`/maintenanceList/${this.state.userID}/${itemId}`);
  sampleRef.remove();
}

  editRow = (row, isSelected, e, id, key) =>
  {
    return (
      <div style={{textAlign: 'center'}}>
      <Icon type="copy" style={{fontSize: '24px'}}
      onClick={() => this.removesample(isSelected.key)}>
        Click me
      </Icon>
      </div>
    )
  }
  editRow1 = (row, isSelected, e, id, key) =>
  {
    return (
      <div style={{textAlign: 'center'}}>
      <Icon type="copy" style={{fontSize: '24px'}}
      onClick={() => this.removesample(isSelected.key)}>
        Click me
      </Icon>
      </div>
    )
  }

  fillParameterInfo = (e, itemId) => {
    e.preventDefault();
    //fire.database().ref('samples') refers to the main title of the fire database.
    this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{
    const parameterListRef = fire.database().ref(`maintenanceList/${user.uid}`);
    let id = fire.database().ref().child(`/maintenanceList/${user.uid}/${itemId}`).key;
    const parameterInfo = {
      Maintenance_Item: this.state.Maintenance_Item,
      Maintenance_Input: '',
      id: id,

    }

    parameterListRef.push(parameterInfo);
    //this.setState is used to clear the text boxes after the form has been submitted.
    this.setState({
      Maintenance_Item: '',

    });

  });
  }

  handleSampleChange = idx => evt => {
    const newParameters = this.state.snapArray1.map((parameter, sidx) => {
      if (idx !== sidx) return parameter;
      return { ...parameter, Maintenance_Input: evt.target.value };
    });
    this.setState({ snapArray1: newParameters });
    console.log(this.state.snapArray1);


    };


    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
          if (!err) {
            console.log('Received values of form: ', values);
          }
        });
      }

      handleConfirmBlur = (e) => {
        const value = e.target.value;
        this.setState({ confirmDirty: this.state.confirmDirty || !!value });
      }

      compareToFirstPassword = (rule, value, callback) => {
        const form = this.props.form;
        if (value && value !== form.getFieldValue('password')) {
          callback('Two passwords that you enter is inconsistent!');
        } else {
          callback();
        }
      }

      validateToNextPassword = (rule, value, callback) => {
        const form = this.props.form;
        if (value && this.state.confirmDirty) {
          form.validateFields(['confirm'], { force: true });
        }
        callback();
      }

      handleWebsiteChange = (value) => {
        let autoCompleteResult;
        if (!value) {
          autoCompleteResult = [];
        } else {
          autoCompleteResult = ['.com', '.org', '.net'].map(domain => `${value}${domain}`);
        }
        this.setState({ autoCompleteResult });
      }






      render() {

        const { getFieldDecorator } = this.props.form;
           const { autoCompleteResult } = this.state;

           const formItemLayout = {
             labelCol: {
               xs: { span: 24 },
               sm: { span: 8 },
             },
             wrapperCol: {
               xs: { span: 24 },
               sm: { span: 16 },
             },
           };
           const tailFormItemLayout = {
             wrapperCol: {
               xs: {
                 span: 24,
                 offset: 0,
               },
               sm: {
                 span: 16,
                 offset: 8,
               },
             },
           };
           const prefixSelector = getFieldDecorator('prefix', {
      initialValue: '86',
    })(
      <Select style={{ width: 70 }}>
        <Option value="86">+86</Option>
        <Option value="87">+87</Option>
      </Select>
    );

    const websiteOptions = autoCompleteResult.map(website => (
      <AutoCompleteOption key={website}>{website}</AutoCompleteOption>
    ));

        let { file } = this.state
        console.log(this.state.file);
        let url = file && URL.createObjectURL(file)

        let img = document.createElement("my-node");




        const MyDoc = (
          <Document>
            <Page size="A4" >

            </Page>
