        progress: 0,
        avatarURL: "",
        userID: "",
        filename: "",
        orders: [],
        reports: [],
        drawings: [],

        documentType: "",
        fileID: "",
        blob: null,
        file:null,
        blobUrl: null,
        upload: '',
        downloadURL: "",
        hover: false,
        border: '',
        fileText: '',


      }



    }




  handleChangeUsername = event =>
    this.setState({ username: event.target.value });
  handleUploadStart = () => this.setState({ isUploading: true, progress: 0 });
  handleProgress = progress => this.setState({ progress });
  handleUploadError = error => {
    this.setState({ isUploading: false });
    console.error(error);
  };

  handleUploadSuccess = () => {


  };

  addPermit = () => {
    this.setState({
      documentType: 'permit',
      avatarURL: this.state.avatarURL,
     });
     console.log(this.state.avatarURL);
     this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{
     const samplesRef = fire.database().ref(`permits/${user.uid}`);
     const metaDataPermit = {
       downloadLink: this.state.avatarURL,
       filename: this.state.filename,
       documentType: this.state.documentType
     }
     samplesRef.push(metaDataPermit);
     });
  }
  addReport = () => {
    this.setState({
      documentType: 'reports',
      avatarURL: this.state.avatarURL,
     });
     console.log(this.state.avatarURL);
     this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{
     const samplesRef = fire.database().ref(`reports/${user.uid}`);
     const metaDataReport = {
       downloadLink: this.state.avatarURL,
       filename: this.state.filename,
       documentType: this.state.documentType
     }
     samplesRef.push(metaDataReport);
     });
  }
  addDrawing = () => {
    this.setState({
      documentType: 'drawings',
      avatarURL: this.state.avatarURL,
     });
     console.log(this.state.avatarURL);
     this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{
     const samplesRef = fire.database().ref(`drawings/${user.uid}`);
     const metaDataDrawings = {
       downloadLink: this.state.avatarURL,
       filename: this.state.filename,
       documentType: this.state.documentType
     }
     samplesRef.push(metaDataDrawings);
     });
  }
  addEquipmentManual = () => {
    this.setState({
      avatarURL: this.state.avatarURL,
     });
     console.log(this.state.avatarURL);
     this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{
     const samplesRef = fire.database().ref(`equipmentManual/${user.uid}`);
     const metaDataEquipmentManual = {
       downloadLink: this.state.avatarURL,
       filename: this.state.filename,
       documentType: this.state.documentType
     }
     samplesRef.push(metaDataEquipmentManual);
     });
  }

  componentDidMount() {

    this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{
      const samplesRef = fire.database().ref(`permits/${user.uid}`);
      samplesRef.on('value', (snapshot) => {
      let orders = snapshot.val();
      console.log(orders);
      let newState = [];
      for (let order in orders) {
        newState.push({
          id: order,
          downloadLink: orders[order].downloadLink,
          filename: orders[order].filename,
        });
      }
      this.setState({
        orders: newState,
      });
    });
  });
  this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{
    const samplesRef = fire.database().ref(`reports/${user.uid}`);
    samplesRef.on('value', (snapshot) => {
    let reports = snapshot.val();
    console.log(reports);
    let newState = [];
    for (let report in reports) {
      newState.push({
        id: report,
        downloadLink: reports[report].downloadLink,
        filename: reports[report].filename,
      });
    }
    this.setState({
      reports: newState,
    });
  });
});

this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{
  const samplesRef = fire.database().ref(`drawings/${user.uid}`);
  samplesRef.on('value', (snapshot) => {
  let drawings = snapshot.val();
  console.log(drawings);
  let newState = [];
  for (let drawing in drawings) {
    newState.push({
      id: drawing,
      downloadLink: drawings[drawing].downloadLink,
      filename: drawings[drawing].filename,
    });
  }
  this.setState({
    drawings: newState,
  });
});

});



  }




  removeReport(itemId) {
    this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{
    const sampleRef = fire.database().ref(`/reports/${user.uid}/${itemId}`);
    sampleRef.remove();
  });
  }
  removeDrawing(itemId) {
    this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{
    const sampleRef = fire.database().ref(`/drawings/${user.uid}/${itemId}`);
    sampleRef.remove();
  });
  }

  deleteDrawing(row, isSelected, e, id) {
    console.log(`${isSelected.id}`);
    return (
      <div style={{textAlign: 'center'}}>
      <Icon type="delete" style={{fontSize: '24px'}}
      onClick={() => this.removeDrawing(`${isSelected.id}`)}>
        Click me
      </Icon>
      </div>
    )
  }
  deleteReport(row, isSelected, e, id) {
    console.log(`${isSelected.id}`);
    return (
      <div style={{textAlign: 'center'}}>
      <Icon type="delete" style={{fontSize: '24px'}}
      onClick={() => this.removeReport(`${isSelected.id}`)}>
        Click me
      </Icon>
      </div>

    )
  }

  editRow(row, isSelected, e, id) {
    console.log(`${isSelected.id}`);
    return (
        <div style={{textAlign: 'center'}}>
      <Icon type="download" style={{fontSize: '24px'}}
      onClick={() => window.open(`${isSelected.downloadLink}`, "_blank")}><p>Download Page</p>

      </Icon>
      </div>
    )
  }







  onDrag = event => {
    event.preventDefault()
  }

  onDrop = event => {
    event.preventDefault()
    let file = event.dataTransfer.files[0]

      this.setState({ file: file, upload: 0, fileText: file.name,  })
      console.log(file);

  }

  upload = () => {

  var filename = this.state.file.name;
  var storageRef = firebase.storage().ref('/images/' + filename);
  var uploadTask = storageRef.put(this.state.file);
  var filename = this.state.file.name;



  // Register three observers:
// 1. 'state_changed' observer, called any time the state changes
// 2. Error observer, called on failure
// 3. Completion observer, called on successful completion
uploadTask.on('state_changed', (snapshot) => {
  // Observe state change events such as progress, pause, and resume
  // Get task progress, including the number of bytes uploaded and the total number of bytes to be uploaded
  var progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
  console.log('Upload is ' + progress + '% done');
  this.setState({ upload: progress });
  switch (snapshot.state) {
    case firebase.storage.TaskState.PAUSED: // or 'paused'
      console.log('Upload is paused');
      break;
    case firebase.storage.TaskState.RUNNING: // or 'running'
      console.log('Upload is running');
      break;
  }
}, function(error) {
  // Handle unsuccessful uploads
}, () => {
  // Handle successful uploads on complete
  // For instance, get the download URL: https://firebasestorage.googleapis.com/...
  message.success(`${this.state.file.name} file uploaded successfully.`);
  uploadTask.snapshot.ref.getDownloadURL().then( (downloadURL) => {
    console.log('File available at', downloadURL);
    this.setState({ downloadURL: downloadURL })
  });
});

  }





uploadFile = (event: any): void => {
    console.log(event.target.files[0]);
    this.setState({
      fileID: event.target.files[0],
    })
}


hoverOn = () => {
      this.setState({ hover: true,
      border: '1.5px dashed #1890ff'  });
    }
    hoverOff = () =>{
      this.setState({ hover: false,
      border: '1px dashed #d9d9d9' });
    }

    onChangeFile = event => {
      event.stopPropagation();
      event.preventDefault();
    var file = event.target.files[0];

        this.setState({ file: file, upload: 0, fileText: file.name,  })
        console.log(file);
    }



  render() {


let { file } = this.state
console.log(this.state.file);
let url = file && URL.createObjectURL(file)


let img = document.createElement("my-node");


    return (
      <Layout>

        <div style={{ background: '#F0F0F0', padding: '5px' }}>
        <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
          <div style={{position: 'relative'}}>
        <Col xs={24} sm={24} md={18} lg={18} xl={18}>
          <h1><b>Upload Documents</b></h1>

        </Col>


      </div>
        </Row>

        </div>

        <div style={{ background: '#F0F0F0', paddingTop: '15px', paddingRight: '5px', paddingLeft: '5px' }}>
        <Row gutter={{ xs: 8, sm: 16, md: 24, lg: 32 }}>
        <Col xs={24} sm={24} md={24} lg={24} xl={24}>


              <Card


              >
              <Tabs defaultActiveKey="1" >
          <TabPane tab="Upload Documents" key="1">
            <Row>
            <Col xs={24} sm={24} md={24} lg={24} xl={24}>
            <Row>

            <Col xs={24} sm={24} md={24} lg={24} xl={24} style={{paddingTop: '20px'}}>

                <p style={{lineHeight: '2px', paddingLeft: '0px', fontSize: '32px'}}><b>UPLOAD DOCUMENTS</b></p>



          </Col>
        </Row>

            <Row>
            <Col xs={24} sm={24} md={24} lg={24} xl={24}>

              <Row>
                <Col span={24} style={{
                    verticalAlign: 'middle', textAlign: 'center',}} onClick={()=>{this.upload.click()}}>
                  <hr></hr>

                    <input id="myInput"
   type="file"
   ref={(ref) => this.upload = ref}
   style={{display: 'none'}}
   onChange={this.onChangeFile.bind(this)}
/>


                    <div
                      onMouseEnter={this.hoverOn}
                      onMouseLeave={this.hoverOff}

                      style={{
    border: this.state.border,

    transition: 'border-color 0.3s',
    cursor: 'pointer',
    borderRadius: '4px',
    textAlign: 'center',
    width: '100%',
    height: '100%',
    position: 'relative',
    backgroundColor: '#fafafa',
}} onDragOver={this.onDrag} onDrop={this.onDrop} onClick={this.fileSelect}>
<div style={{
    verticalAlign: 'middle', textAlign: 'center',}}>

                  <Icon style={{fontSize: '48px', color: '#1890ff'}} type="inbox" />
                  <p><b>Drop an image!</b></p>
                    <p className="ant-upload-text">Click or drag file to this area to upload</p>
                    <p className="ant-upload-hint">Support for a single  upload. Strictly prohibit from uploading company data or other band files</p>
                  </div>
                </div>
                <Row style={{paddingTop: '10px'}} ><Progress type="dashboard" percent={this.state.upload} /></Row>
                <Row style={{paddingTop: '10px'}}><b>{this.state.fileText}</b></Row>
                <Row style={{paddingTop: '10px'}}><Button onSelect={this.upload}>Upload</Button></Row>


          </Col>
            </Row>

    <Row>
      <form>
      <FormGroup>
        <Row>











        </Row>



