import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import ReactDOM from 'react-dom';
import Request from 'superagent';

import firebase from 'firebase';
import { fire } from '../fire';

import { Row, Col, Tabs, Table, Divider, Tag, message, Card, Drawer, Menu, Dropdown, Button, Layout, Carousel, Input, Popover, Icon, Cascader, Switch, Select, AutoComplete, Radio } from 'antd';


const TabPane = Tabs.TabPane;


export default class dashForecast extends Component {

  constructor(props) {
      super(props);
        this.state = {
        userID: '',

      }
    }



   snapshotToArray(snapshot) {
      var returnArr = [];

      snapshot.forEach(function(childSnapshot) {
          var item = childSnapshot.val();
          item.key = childSnapshot.key;
          returnArr.push(item);
      });
      return returnArr;
  };


  async componentDidMount() {
    this.removeAuthListener = fire.auth().onAuthStateChanged(user=>{

      this.setState({
        userID: user.uid,
      })

           console.log(this.state.center);
                     var myLat = `${this.state.latitude}`;
                       var myLon = `${this.state.longitude}`;
                    let API_WEATHER = `https://api.weather.gov/points/39.7456,-97.0892/forecast`;


                    fetch(API_WEATHER)
                 .then(response => response.json())
                 .then(responseJson => {
                   console.log(responseJson);

                 })
                 .catch(error => {
                   console.log(error);
                 });

      })

  }


  render() {


    return (

      <Layout>

        <Row>
        <Col>

          <Card title="Card title" bordered={false} >
        <p>Card content</p>
        <p>Card content</p>
        <p>Card content</p>
      </Card>

    </Col>
  </Row>



      </Layout>



    )
  }
}
